    private PasswordUtil passwordUtil;

    @Autowired
    private UserDao userDao;

    @Autowired
    private IncomeDao incomeDao;

    @Autowired
    private ExpenseDao expenseDao;

    public UsersEntity getUserbyEmail(String email) {
        return userDao.getUserbyEmail(email);
    }

    public UsersEntity checkUser(UsersEntity user) {
        if(user == null) return null;
        UsersEntity userFound = userDao.getUserbyEmail(user.getEmail());
        if(userFound == null) {
            return null;
        } else if(!user.getPassword().equals(userFound.getPassword())) {
            return null;
        } else {
            return userFound;
        }
    }

    public UsersEntity registerUser(UsersEntity user) {
        return userDao.registerUser(user);
    }

    public IncomeEntity addIncome(IncomeEntity income)
    {
        return incomeDao.addIncome(income);
    }

    public ExpenseEntity addExpense(ExpenseEntity expense)
    {
        return expenseDao.addExpense(expense);
    }

    public List<IncomeEntity> getAllIncome(int userId) {
        return incomeDao.getAllIncome(userId);
    }

    public List<ExpenseEntity> getAllExpense(int userId) {
        return  expenseDao.getAllExpense(userId);
    }

    public Integer getExpenseTotalAmount(List<ExpenseEntity> expenceList){
        return expenceList.stream().mapToInt(ExpenseEntity::getAmount).sum();
/*        // before java 8
        sum = 0;
        for(int i = 0; i < expenceList.size() ; i++){
            sum += expenceList.get(i).getAmount();
        }
        return sum;*/
    }

    public Integer getIncomeTotalAmount(List<IncomeEntity> incomeList){
        return incomeList.stream().mapToInt(IncomeEntity::getAmount).sum();
    }

    public String validate(UsersEntity user, String password2) {
        String error = null;

        if(StringUtils.isEmpty(user.getName())) {
            error = "You have to enter a username";
        } else if(!EMAIL_ADDRESS_REGEX.matcher(user.getEmail()).matches()) {
            error = "You have to enter a valid email address";
        } else if(StringUtils.isEmpty(user.getPassword())) {
            error = "You have to enter a password";
        } else if(!user.getPassword().equals(password2)) {
            error = "The two passwords do not match";
        }

        return error;
    }



